import{Da as s,Ra as t,Sa as n,Ta as r,bb as e}from"./chunk-AMTD2DMX.js";var d=class u{static \u0275fac=function(i){return new(i||u)};static \u0275cmp=s({type:u,selectors:[["app-legalnotice"]],decls:421,vars:0,consts:[["id","m716"],[1,"index"],["href","#m716",1,"index-link"],["href","#m3",1,"index-link"],["href","#mOverview",1,"index-link"],["href","#m2427",1,"index-link"],["href","#m27",1,"index-link"],["href","#m25",1,"index-link"],["href","#m24",1,"index-link"],["href","#m12",1,"index-link"],["href","#m10",1,"index-link"],["href","#m225",1,"index-link"],["href","#m134",1,"index-link"],["href","#m367",1,"index-link"],["href","#m15",1,"index-link"],["href","#m42",1,"index-link"],["id","m3"],["href","mailto:nysascha1@gmail.com"],["id","mOverview"],["id","m2427"],["id","m27"],["id","m25"],["id","m24"],["href","https://www.dataprivacyframework.gov/","target","_blank"],["href","https://commission.europa.eu/law/law-topic/data-protection/international-dimension-data-protection_en?prefLang=de","target","_blank"],["id","m12"],[1,"m-elements"],["id","m10"],["id","m225"],[1,""],["id","m134"],["id","m367"],["id","m15"],["id","m42"],[1,"glossary"],[1,"seal"],["href","https://datenschutz-generator.de/","title","Rechtstext von Dr. Schwenke - f\xFCr weitere Informationen bitte anklicken.","target","_blank","rel","noopener noreferrer nofollow"]],template:function(i,l){i&1&&(t(0,"div")(1,"h1"),e(2,"Datenschutzerkl\xE4rung"),n(),t(3,"h2",0),e(4,"Pr\xE4ambel"),n(),t(5,"p"),e(6,' Mit der folgenden Datenschutzerkl\xE4rung m\xF6chten wir Sie dar\xFCber aufkl\xE4ren, welche Arten Ihrer personenbezogenen Daten (nachfolgend auch kurz als "Daten" bezeichnet) wir zu welchen Zwecken und in welchem Umfang verarbeiten. Die Datenschutzerkl\xE4rung gilt f\xFCr alle von uns durchgef\xFChrten Verarbeitungen personenbezogener Daten, sowohl im Rahmen der Erbringung unserer Leistungen als auch insbesondere auf unseren Webseiten, in mobilen Applikationen sowie innerhalb externer Onlinepr\xE4senzen, wie z.\xA0B. unserer Social-Media-Profile (nachfolgend zusammenfassend bezeichnet als "Onlineangebot"). '),n(),t(7,"p"),e(8,"Die verwendeten Begriffe sind nicht geschlechtsspezifisch."),n(),t(9,"p"),e(10,"Stand: 10. Juni 2025"),n(),t(11,"h2"),e(12,"Inhalts\xFCbersicht"),n(),t(13,"ul",1)(14,"li")(15,"a",2),e(16,"Pr\xE4ambel"),n()(),t(17,"li")(18,"a",3),e(19,"Verantwortlicher"),n()(),t(20,"li")(21,"a",4),e(22,"\xDCbersicht der Verarbeitungen"),n()(),t(23,"li")(24,"a",5),e(25,"Ma\xDFgebliche Rechtsgrundlagen"),n()(),t(26,"li")(27,"a",6),e(28,"Sicherheitsma\xDFnahmen"),n()(),t(29,"li")(30,"a",7),e(31,"\xDCbermittlung von personenbezogenen Daten"),n()(),t(32,"li")(33,"a",8),e(34,"Internationale Datentransfers"),n()(),t(35,"li")(36,"a",9),e(37,"Allgemeine Informationen zur Datenspeicherung und L\xF6schung"),n()(),t(38,"li")(39,"a",10),e(40,"Rechte der betroffenen Personen"),n()(),t(41,"li")(42,"a",11),e(43,"Bereitstellung des Onlineangebots und Webhosting"),n()(),t(44,"li")(45,"a",12),e(46,"Einsatz von Cookies"),n()(),t(47,"li")(48,"a",13),e(49,"Registrierung, Anmeldung und Nutzerkonto"),n()(),t(50,"li")(51,"a",14),e(52,"\xC4nderung und Aktualisierung"),n()(),t(53,"li")(54,"a",15),e(55,"Begriffsdefinitionen"),n()()(),t(56,"h2",16),e(57,"Verantwortlicher"),n(),t(58,"p"),e(59,"Sascha Ny\xDFen"),r(60,"br"),e(61,"Stettinerstrasse 1"),r(62,"br"),e(63,"92224 Amberg"),n(),t(64,"p"),e(65," E-Mail-Adresse: "),t(66,"a",17),e(67,"nysascha1@gmail.com"),n()(),t(68,"h2",18),e(69,"\xDCbersicht der Verarbeitungen"),n(),t(70,"p"),e(71," Die nachfolgende \xDCbersicht fasst die Arten der verarbeiteten Daten und die Zwecke ihrer Verarbeitung zusammen und verweist auf die betroffenen Personen. "),n(),t(72,"h3"),e(73,"Arten der verarbeiteten Daten"),n(),t(74,"ul")(75,"li"),e(76,"Bestandsdaten."),n(),t(77,"li"),e(78,"Kontaktdaten."),n(),t(79,"li"),e(80,"Inhaltsdaten."),n(),t(81,"li"),e(82,"Nutzungsdaten."),n(),t(83,"li"),e(84,"Meta-, Kommunikations- und Verfahrensdaten."),n(),t(85,"li"),e(86,"Protokolldaten."),n()(),t(87,"h3"),e(88,"Kategorien betroffener Personen"),n(),t(89,"ul")(90,"li"),e(91,"Nutzer."),n()(),t(92,"h3"),e(93,"Zwecke der Verarbeitung"),n(),t(94,"ul")(95,"li"),e(96," Erbringung vertraglicher Leistungen und Erf\xFCllung vertraglicher Pflichten. "),n(),t(97,"li"),e(98,"Sicherheitsma\xDFnahmen."),n(),t(99,"li"),e(100,"Organisations- und Verwaltungsverfahren."),n(),t(101,"li"),e(102,"Bereitstellung unseres Onlineangebotes und Nutzerfreundlichkeit."),n(),t(103,"li"),e(104,"Informationstechnische Infrastruktur."),n()(),t(105,"h2",19),e(106,"Ma\xDFgebliche Rechtsgrundlagen"),n(),t(107,"p")(108,"strong"),e(109,"Ma\xDFgebliche Rechtsgrundlagen nach der DSGVO: "),n(),e(110,"Im Folgenden erhalten Sie eine \xDCbersicht der Rechtsgrundlagen der DSGVO, auf deren Basis wir personenbezogene Daten verarbeiten. Bitte nehmen Sie zur Kenntnis, dass neben den Regelungen der DSGVO nationale Datenschutzvorgaben in Ihrem bzw. unserem Wohn- oder Sitzland gelten k\xF6nnen. Sollten ferner im Einzelfall speziellere Rechtsgrundlagen ma\xDFgeblich sein, teilen wir Ihnen diese in der Datenschutzerkl\xE4rung mit. "),n(),t(111,"ul")(112,"li")(113,"strong"),e(114,"Einwilligung (Art. 6 Abs. 1 S. 1 lit. a) DSGVO)"),n(),e(115," - Die betroffene Person hat ihre Einwilligung in die Verarbeitung der sie betreffenden personenbezogenen Daten f\xFCr einen spezifischen Zweck oder mehrere bestimmte Zwecke gegeben. "),n(),t(116,"li")(117,"strong"),e(118,"Vertragserf\xFCllung und vorvertragliche Anfragen (Art. 6 Abs. 1 S. 1 lit. b) DSGVO)"),n(),e(119," - Die Verarbeitung ist f\xFCr die Erf\xFCllung eines Vertrags, dessen Vertragspartei die betroffene Person ist, oder zur Durchf\xFChrung vorvertraglicher Ma\xDFnahmen erforderlich, die auf Anfrage der betroffenen Person erfolgen. "),n(),t(120,"li")(121,"strong"),e(122,"Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO)"),n(),e(123," - die Verarbeitung ist zur Wahrung der berechtigten Interessen des Verantwortlichen oder eines Dritten notwendig, vorausgesetzt, dass die Interessen, Grundrechte und Grundfreiheiten der betroffenen Person, die den Schutz personenbezogener Daten verlangen, nicht \xFCberwiegen. "),n()(),t(124,"p")(125,"strong"),e(126,"Nationale Datenschutzregelungen in Deutschland: "),n(),e(127,"Zus\xE4tzlich zu den Datenschutzregelungen der DSGVO gelten nationale Regelungen zum Datenschutz in Deutschland. Hierzu geh\xF6rt insbesondere das Gesetz zum Schutz vor Missbrauch personenbezogener Daten bei der Datenverarbeitung (Bundesdatenschutzgesetz \u2013 BDSG). Das BDSG enth\xE4lt insbesondere Spezialregelungen zum Recht auf Auskunft, zum Recht auf L\xF6schung, zum Widerspruchsrecht, zur Verarbeitung besonderer Kategorien personenbezogener Daten, zur Verarbeitung f\xFCr andere Zwecke und zur \xDCbermittlung sowie automatisierten Entscheidungsfindung im Einzelfall einschlie\xDFlich Profiling. Ferner k\xF6nnen Landesdatenschutzgesetze der einzelnen Bundesl\xE4nder zur Anwendung gelangen. "),n(),t(128,"h2",20),e(129,"Sicherheitsma\xDFnahmen"),n(),t(130,"p"),e(131," Wir treffen nach Ma\xDFgabe der gesetzlichen Vorgaben unter Ber\xFCcksichtigung des Stands der Technik, der Implementierungskosten und der Art, des Umfangs, der Umst\xE4nde und der Zwecke der Verarbeitung sowie der unterschiedlichen Eintrittswahrscheinlichkeiten und des Ausma\xDFes der Bedrohung der Rechte und Freiheiten nat\xFCrlicher Personen geeignete technische und organisatorische Ma\xDFnahmen, um ein dem Risiko angemessenes Schutzniveau zu gew\xE4hrleisten. "),n(),t(132,"p"),e(133," Zu den Ma\xDFnahmen geh\xF6ren insbesondere die Sicherung der Vertraulichkeit, Integrit\xE4t und Verf\xFCgbarkeit von Daten durch Kontrolle des physischen und elektronischen Zugangs zu den Daten als auch des sie betreffenden Zugriffs, der Eingabe, der Weitergabe, der Sicherung der Verf\xFCgbarkeit und ihrer Trennung. Des Weiteren haben wir Verfahren eingerichtet, die eine Wahrnehmung von Betroffenenrechten, die L\xF6schung von Daten und Reaktionen auf die Gef\xE4hrdung der Daten gew\xE4hrleisten. Ferner ber\xFCcksichtigen wir den Schutz personenbezogener Daten bereits bei der Entwicklung bzw. Auswahl von Hardware, Software sowie Verfahren entsprechend dem Prinzip des Datenschutzes, durch Technikgestaltung und durch datenschutzfreundliche Voreinstellungen. "),n(),t(134,"p"),e(135," Sicherung von Online-Verbindungen durch TLS-/SSL-Verschl\xFCsselungstechnologie (HTTPS): Um die Daten der Nutzer, die \xFCber unsere Online-Dienste \xFCbertragen werden, vor unerlaubten Zugriffen zu sch\xFCtzen, setzen wir auf die TLS-/SSL-Verschl\xFCsselungstechnologie. Secure Sockets Layer (SSL) und Transport Layer Security (TLS) sind die Eckpfeiler der sicheren Daten\xFCbertragung im Internet. Diese Technologien verschl\xFCsseln die Informationen, die zwischen der Website oder App und dem Browser des Nutzers (oder zwischen zwei Servern) \xFCbertragen werden, wodurch die Daten vor unbefugtem Zugriff gesch\xFCtzt sind. TLS, als die weiterentwickelte und sicherere Version von SSL, gew\xE4hrleistet, dass alle Daten\xFCbertragungen den h\xF6chsten Sicherheitsstandards entsprechen. Wenn eine Website durch ein SSL-/TLS-Zertifikat gesichert ist, wird dies durch die Anzeige von HTTPS in der URL signalisiert. Dies dient als ein Indikator f\xFCr die Nutzer, dass ihre Daten sicher und verschl\xFCsselt \xFCbertragen werden. "),n(),t(136,"h2",21),e(137,"\xDCbermittlung von personenbezogenen Daten"),n(),t(138,"p"),e(139," Im Rahmen unserer Verarbeitung von personenbezogenen Daten kommt es vor, dass diese an andere Stellen, Unternehmen, rechtlich selbstst\xE4ndige Organisationseinheiten oder Personen \xFCbermittelt beziehungsweise ihnen gegen\xFCber offengelegt werden. Zu den Empf\xE4ngern dieser Daten k\xF6nnen z.\xA0B. mit IT-Aufgaben beauftragte Dienstleister geh\xF6ren oder Anbieter von Diensten und Inhalten, die in eine Website eingebunden sind. In solchen F\xE4llen beachten wir die gesetzlichen Vorgaben und schlie\xDFen insbesondere entsprechende Vertr\xE4ge bzw. Vereinbarungen, die dem Schutz Ihrer Daten dienen, mit den Empf\xE4ngern Ihrer Daten ab. "),n(),t(140,"h2",22),e(141,"Internationale Datentransfers"),n(),t(142,"p"),e(143," Datenverarbeitung in Drittl\xE4ndern: Sofern wir Daten in ein Drittland (d. h. au\xDFerhalb der Europ\xE4ischen Union (EU) oder des Europ\xE4ischen Wirtschaftsraums (EWR)) \xFCbermitteln oder dies im Rahmen der Nutzung von Diensten Dritter oder der Offenlegung bzw. \xDCbermittlung von Daten an andere Personen, Stellen oder Unternehmen geschieht (was erkennbar wird anhand der Postadresse des jeweiligen Anbieters oder wenn in der Datenschutzerkl\xE4rung ausdr\xFCcklich auf den Datentransfer in Drittl\xE4nder hingewiesen wird), erfolgt dies stets im Einklang mit den gesetzlichen Vorgaben. "),n(),t(144,"p"),e(145," F\xFCr Daten\xFCbermittlungen in die USA st\xFCtzen wir uns vorrangig auf das Data Privacy Framework (DPF), welches durch einen Angemessenheitsbeschluss der EU-Kommission vom 10.07.2023 als sicherer Rechtsrahmen anerkannt wurde. Zus\xE4tzlich haben wir mit den jeweiligen Anbietern Standardvertragsklauseln abgeschlossen, die den Vorgaben der EU-Kommission entsprechen und vertragliche Verpflichtungen zum Schutz Ihrer Daten festlegen. "),n(),t(146,"p"),e(147," Diese zweifache Absicherung gew\xE4hrleistet einen umfassenden Schutz Ihrer Daten: Das DPF bildet die prim\xE4re Schutzebene, w\xE4hrend die Standardvertragsklauseln als zus\xE4tzliche Sicherheit dienen. Sollten sich \xC4nderungen im Rahmen des DPF ergeben, greifen die Standardvertragsklauseln als zuverl\xE4ssige R\xFCckfalloption ein. So stellen wir sicher, dass Ihre Daten auch bei etwaigen politischen oder rechtlichen Ver\xE4nderungen stets angemessen gesch\xFCtzt bleiben. "),n(),t(148,"p"),e(149," Bei den einzelnen Diensteanbietern informieren wir Sie dar\xFCber, ob sie nach dem DPF zertifiziert sind und ob Standardvertragsklauseln vorliegen. Weitere Informationen zum DPF und eine Liste der zertifizierten Unternehmen finden Sie auf der Website des US-Handelsministeriums unter "),t(150,"a",23),e(151,"https://www.dataprivacyframework.gov/"),n(),e(152," (in englischer Sprache). "),n(),t(153,"p"),e(154," F\xFCr Daten\xFCbermittlungen in andere Drittl\xE4nder gelten entsprechende Sicherheitsma\xDFnahmen, insbesondere Standardvertragsklauseln, ausdr\xFCckliche Einwilligungen oder gesetzlich erforderliche \xDCbermittlungen. Informationen zu Drittlandtransfers und geltenden Angemessenheitsbeschl\xFCssen k\xF6nnen Sie dem Informationsangebot der EU-Kommission entnehmen: "),t(155,"a",24),e(156,"https://commission.europa.eu/law/law-topic/data-protection/international-dimension-data-protection_en?prefLang=de."),n()(),t(157,"h2",25),e(158,"Allgemeine Informationen zur Datenspeicherung und L\xF6schung"),n(),t(159,"p"),e(160," Wir l\xF6schen personenbezogene Daten, die wir verarbeiten, gem\xE4\xDF den gesetzlichen Bestimmungen, sobald die zugrundeliegenden Einwilligungen widerrufen werden oder keine weiteren rechtlichen Grundlagen f\xFCr die Verarbeitung bestehen. Dies betrifft F\xE4lle, in denen der urspr\xFCngliche Verarbeitungszweck entf\xE4llt oder die Daten nicht mehr ben\xF6tigt werden. Ausnahmen von dieser Regelung bestehen, wenn gesetzliche Pflichten oder besondere Interessen eine l\xE4ngere Aufbewahrung oder Archivierung der Daten erfordern. "),n(),t(161,"p"),e(162," Insbesondere m\xFCssen Daten, die aus handels- oder steuerrechtlichen Gr\xFCnden aufbewahrt werden m\xFCssen oder deren Speicherung notwendig ist zur Rechtsverfolgung oder zum Schutz der Rechte anderer nat\xFCrlicher oder juristischer Personen, entsprechend archiviert werden. "),n(),t(163,"p"),e(164," Unsere Datenschutzhinweise enthalten zus\xE4tzliche Informationen zur Aufbewahrung und L\xF6schung von Daten, die speziell f\xFCr bestimmte Verarbeitungsprozesse gelten. "),n(),t(165,"p"),e(166," Bei mehreren Angaben zur Aufbewahrungsdauer oder L\xF6schungsfristen eines Datums, ist stets die l\xE4ngste Frist ma\xDFgeblich. "),n(),t(167,"p"),e(168," Beginnt eine Frist nicht ausdr\xFCcklich zu einem bestimmten Datum und betr\xE4gt sie mindestens ein Jahr, so startet sie automatisch am Ende des Kalenderjahres, in dem das fristausl\xF6sende Ereignis eingetreten ist. Im Fall laufender Vertragsverh\xE4ltnisse, in deren Rahmen Daten gespeichert werden, ist das fristausl\xF6sende Ereignis der Zeitpunkt des Wirksamwerdens der K\xFCndigung oder sonstige Beendigung des Rechtsverh\xE4ltnisses. "),n(),t(169,"p"),e(170," Daten, die nicht mehr f\xFCr den urspr\xFCnglich vorgesehenen Zweck, sondern aufgrund gesetzlicher Vorgaben oder anderer Gr\xFCnde aufbewahrt werden, verarbeiten wir ausschlie\xDFlich zu den Gr\xFCnden, die ihre Aufbewahrung rechtfertigen. "),n(),t(171,"p")(172,"strong"),e(173,"Weitere Hinweise zu Verarbeitungsprozessen, Verfahren und Diensten:"),n()(),t(174,"ul",26)(175,"li")(176,"strong"),e(177,"Aufbewahrung und L\xF6schung von Daten: "),n(),e(178,"Die folgenden allgemeinen Fristen gelten f\xFCr die Aufbewahrung und Archivierung nach deutschem Recht: "),t(179,"ul")(180,"li"),e(181," 10 Jahre - Aufbewahrungsfrist f\xFCr B\xFCcher und Aufzeichnungen, Jahresabschl\xFCsse, Inventare, Lageberichte, Er\xF6ffnungsbilanz sowie die zu ihrem Verst\xE4ndnis erforderlichen Arbeitsanweisungen und sonstigen Organisationsunterlagen (\xA7 147 Abs. 1 Nr. 1 i.V.m. Abs. 3 AO, \xA7 14b Abs. 1 UStG, \xA7 257 Abs. 1 Nr. 1 i.V.m. Abs. 4 HGB). "),n(),t(182,"li"),e(183," 8 Jahre - Buchungsbelege, wie z.\xA0B. Rechnungen und Kostenbelege (\xA7 147 Abs. 1 Nr. 4 und 4a i.V.m. Abs. 3 Satz 1 AO sowie \xA7 257 Abs. 1 Nr. 4 i.V.m. Abs. 4 HGB). "),n(),t(184,"li"),e(185," 6 Jahre - \xDCbrige Gesch\xE4ftsunterlagen: empfangene Handels- oder Gesch\xE4ftsbriefe, Wiedergaben der abgesandten Handels- oder Gesch\xE4ftsbriefe, sonstige Unterlagen, soweit sie f\xFCr die Besteuerung von Bedeutung sind, z.\xA0B. Stundenlohnzettel, Betriebsabrechnungsb\xF6gen, Kalkulationsunterlagen, Preisauszeichnungen, aber auch Lohnabrechnungsunterlagen, soweit sie nicht bereits Buchungsbelege sind und Kassenstreifen (\xA7 147 Abs. 1 Nr. 2, 3, 5 i.V.m. Abs. 3 AO, \xA7 257 Abs. 1 Nr. 2 u. 3 i.V.m. Abs. 4 HGB). "),n(),t(186,"li"),e(187," 3 Jahre - Daten, die erforderlich sind, um potenzielle Gew\xE4hrleistungs- und Schadensersatzanspr\xFCche oder \xE4hnliche vertragliche Anspr\xFCche und Rechte zu ber\xFCcksichtigen sowie damit verbundene Anfragen zu bearbeiten, basierend auf fr\xFCheren Gesch\xE4ftserfahrungen und \xFCblichen Branchenpraktiken, werden f\xFCr die Dauer der regul\xE4ren gesetzlichen Verj\xE4hrungsfrist von drei Jahren gespeichert (\xA7\xA7 195, 199 BGB). "),n()()()(),t(188,"h2",27),e(189,"Rechte der betroffenen Personen"),n(),t(190,"p"),e(191," Rechte der betroffenen Personen aus der DSGVO: Ihnen stehen als Betroffene nach der DSGVO verschiedene Rechte zu, die sich insbesondere aus Art. 15 bis 21 DSGVO ergeben: "),n(),t(192,"ul")(193,"li")(194,"strong"),e(195,"Widerspruchsrecht: Sie haben das Recht, aus Gr\xFCnden, die sich aus Ihrer besonderen Situation ergeben, jederzeit gegen die Verarbeitung der Sie betreffenden personenbezogenen Daten, die aufgrund von Art. 6 Abs. 1 lit. e oder f DSGVO erfolgt, Widerspruch einzulegen; dies gilt auch f\xFCr ein auf diese Bestimmungen gest\xFCtztes Profiling. Werden die Sie betreffenden personenbezogenen Daten verarbeitet, um Direktwerbung zu betreiben, haben Sie das Recht, jederzeit Widerspruch gegen die Verarbeitung der Sie betreffenden personenbezogenen Daten zum Zwecke derartiger Werbung einzulegen; dies gilt auch f\xFCr das Profiling, soweit es mit solcher Direktwerbung in Verbindung steht."),n()(),t(196,"li")(197,"strong"),e(198,"Widerrufsrecht bei Einwilligungen:"),n(),e(199," Sie haben das Recht, erteilte Einwilligungen jederzeit zu widerrufen. "),n(),t(200,"li")(201,"strong"),e(202,"Auskunftsrecht:"),n(),e(203," Sie haben das Recht, eine Best\xE4tigung dar\xFCber zu verlangen, ob betreffende Daten verarbeitet werden und auf Auskunft \xFCber diese Daten sowie auf weitere Informationen und Kopie der Daten entsprechend den gesetzlichen Vorgaben. "),n(),t(204,"li")(205,"strong"),e(206,"Recht auf Berichtigung:"),n(),e(207," Sie haben entsprechend den gesetzlichen Vorgaben das Recht, die Vervollst\xE4ndigung der Sie betreffenden Daten oder die Berichtigung der Sie betreffenden unrichtigen Daten zu verlangen. "),n(),t(208,"li")(209,"strong"),e(210,"Recht auf L\xF6schung und Einschr\xE4nkung der Verarbeitung:"),n(),e(211," Sie haben nach Ma\xDFgabe der gesetzlichen Vorgaben das Recht, zu verlangen, dass Sie betreffende Daten unverz\xFCglich gel\xF6scht werden, bzw. alternativ nach Ma\xDFgabe der gesetzlichen Vorgaben eine Einschr\xE4nkung der Verarbeitung der Daten zu verlangen. "),n(),t(212,"li")(213,"strong"),e(214,"Recht auf Daten\xFCbertragbarkeit:"),n(),e(215," Sie haben das Recht, Sie betreffende Daten, die Sie uns bereitgestellt haben, nach Ma\xDFgabe der gesetzlichen Vorgaben in einem strukturierten, g\xE4ngigen und maschinenlesbaren Format zu erhalten oder deren \xDCbermittlung an einen anderen Verantwortlichen zu fordern. "),n(),t(216,"li")(217,"strong"),e(218,"Beschwerde bei Aufsichtsbeh\xF6rde:"),n(),e(219," Sie haben unbeschadet eines anderweitigen verwaltungsrechtlichen oder gerichtlichen Rechtsbehelfs das Recht auf Beschwerde bei einer Aufsichtsbeh\xF6rde, insbesondere in dem Mitgliedstaat ihres gew\xF6hnlichen Aufenthaltsorts, ihres Arbeitsplatzes oder des Orts des mutma\xDFlichen Versto\xDFes, wenn Sie der Ansicht sind, dass die Verarbeitung der Sie betreffenden personenbezogenen Daten gegen die Vorgaben der DSGVO verst\xF6\xDFt. "),n()(),t(220,"h2",28),e(221,"Bereitstellung des Onlineangebots und Webhosting"),n(),t(222,"p"),e(223," Wir verarbeiten die Daten der Nutzer, um ihnen unsere Online-Dienste zur Verf\xFCgung stellen zu k\xF6nnen. Zu diesem Zweck verarbeiten wir die IP-Adresse des Nutzers, die notwendig ist, um die Inhalte und Funktionen unserer Online-Dienste an den Browser oder das Endger\xE4t der Nutzer zu \xFCbermitteln. "),n(),t(224,"ul",26)(225,"li")(226,"strong"),e(227,"Verarbeitete Datenarten:"),n(),e(228," Nutzungsdaten (z. B. Seitenaufrufe und Verweildauer, Klickpfade, Nutzungsintensit\xE4t und -frequenz, verwendete Ger\xE4tetypen und Betriebssysteme, Interaktionen mit Inhalten und Funktionen); Meta-, Kommunikations- und Verfahrensdaten (z. B. IP-Adressen, Zeitangaben, Identifikationsnummern, beteiligte Personen). Protokolldaten (z.\xA0B. Logfiles betreffend Logins oder den Abruf von Daten oder Zugriffszeiten.). "),n(),t(229,"li")(230,"strong"),e(231,"Betroffene Personen:"),n(),e(232," Nutzer (z.\xA0B. Webseitenbesucher, Nutzer von Onlinediensten). "),n(),t(233,"li")(234,"strong"),e(235,"Zwecke der Verarbeitung:"),n(),e(236," Bereitstellung unseres Onlineangebotes und Nutzerfreundlichkeit; Informationstechnische Infrastruktur (Betrieb und Bereitstellung von Informationssystemen und technischen Ger\xE4ten (Computer, Server etc.)). Sicherheitsma\xDFnahmen. "),n(),t(237,"li")(238,"strong"),e(239,"Aufbewahrung und L\xF6schung:"),n(),e(240,' L\xF6schung entsprechend Angaben im Abschnitt "Allgemeine Informationen zur Datenspeicherung und L\xF6schung". '),n(),t(241,"li",29)(242,"strong"),e(243,"Rechtsgrundlagen:"),n(),e(244," Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO). "),n()(),t(245,"p")(246,"strong"),e(247,"Weitere Hinweise zu Verarbeitungsprozessen, Verfahren und Diensten:"),n()(),t(248,"ul",26)(249,"li")(250,"strong"),e(251,"Bereitstellung Onlineangebot auf gemietetem Speicherplatz: "),n(),e(252,'F\xFCr die Bereitstellung unseres Onlineangebotes nutzen wir Speicherplatz, Rechenkapazit\xE4t und Software, die wir von einem entsprechenden Serveranbieter (auch "Webhoster" genannt) mieten oder anderweitig beziehen; '),t(253,"span",29)(254,"strong"),e(255,"Rechtsgrundlagen:"),n(),e(256," Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO)."),n()(),t(257,"li")(258,"strong"),e(259,"Erhebung von Zugriffsdaten und Logfiles: "),n(),e(260,'Der Zugriff auf unser Onlineangebot wird in Form von sogenannten "Server-Logfiles" protokolliert. Zu den Serverlogfiles k\xF6nnen die Adresse und der Name der abgerufenen Webseiten und Dateien, Datum und Uhrzeit des Abrufs, \xFCbertragene Datenmengen, Meldung \xFCber erfolgreichen Abruf, Browsertyp nebst Version, das Betriebssystem des Nutzers, Referrer URL (die zuvor besuchte Seite) und im Regelfall IP-Adressen und der anfragende Provider geh\xF6ren. Die Serverlogfiles k\xF6nnen zum einen zu Sicherheitszwecken eingesetzt werden, z.\xA0B. um eine \xDCberlastung der Server zu vermeiden (insbesondere im Fall von missbr\xE4uchlichen Angriffen, sogenannten DDoS-Attacken), und zum anderen, um die Auslastung der Server und ihre Stabilit\xE4t sicherzustellen; '),t(261,"span",29)(262,"strong"),e(263,"Rechtsgrundlagen:"),n(),e(264," Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO). "),n(),t(265,"strong"),e(266,"L\xF6schung von Daten:"),n(),e(267," Logfile-Informationen werden f\xFCr die Dauer von maximal 30 Tagen gespeichert und danach gel\xF6scht oder anonymisiert. Daten, deren weitere Aufbewahrung zu Beweiszwecken erforderlich ist, sind bis zur endg\xFCltigen Kl\xE4rung des jeweiligen Vorfalls von der L\xF6schung ausgenommen. "),n()(),t(268,"h2",30),e(269,"Einsatz von Cookies"),n(),t(270,"p"),e(271,' Unter dem Begriff \u201ECookies" werden Funktionen, die Informationen auf Endger\xE4ten der Nutzer speichern und aus ihnen auslesen, verstanden. Cookies k\xF6nnen ferner in Bezug auf unterschiedliche Anliegen Einsatz finden, etwa zu Zwecken der Funktionsf\xE4higkeit, der Sicherheit und des Komforts von Onlineangeboten sowie der Erstellung von Analysen der Besucherstr\xF6me. Wir verwenden Cookies gem\xE4\xDF den gesetzlichen Vorschriften. Dazu holen wir, wenn erforderlich, vorab die Zustimmung der Nutzer ein. Ist eine Zustimmung nicht notwendig, setzen wir auf unsere berechtigten Interessen. Dies gilt, wenn das Speichern und Auslesen von Informationen unerl\xE4sslich ist, um ausdr\xFCcklich angeforderte Inhalte und Funktionen bereitstellen zu k\xF6nnen. Dazu z\xE4hlen etwa die Speicherung von Einstellungen sowie die Sicherstellung der Funktionalit\xE4t und Sicherheit unseres Onlineangebots. Die Einwilligung kann jederzeit widerrufen werden. Wir informieren klar \xFCber deren Umfang und welche Cookies genutzt werden. '),n(),t(272,"p")(273,"strong"),e(274,"Hinweise zu datenschutzrechtlichen Rechtsgrundlagen: "),n(),e(275,"Ob wir personenbezogene Daten mithilfe von Cookies verarbeiten, h\xE4ngt von einer Einwilligung ab. Liegt eine Einwilligung vor, dient sie als Rechtsgrundlage. Ohne Einwilligung st\xFCtzen wir uns auf unsere berechtigten Interessen, die vorstehend in diesem Abschnitt und im Kontext der jeweiligen Dienste und Verfahren erl\xE4utert sind. "),n(),t(276,"p")(277,"strong"),e(278,"Speicherdauer:\xA0"),n(),e(279,"Im Hinblick auf die Speicherdauer werden die folgenden Arten von Cookies unterschieden: "),n(),t(280,"ul")(281,"li")(282,"strong"),e(283,"Tempor\xE4re Cookies (auch: Session- oder Sitzungscookies):"),n(),e(284," Tempor\xE4re Cookies werden sp\xE4testens gel\xF6scht, nachdem ein Nutzer ein Onlineangebot verlassen und sein Endger\xE4t (z.\xA0B. Browser oder mobile Applikation) geschlossen hat. "),n(),t(285,"li")(286,"strong"),e(287,"Permanente Cookies:"),n(),e(288," Permanente Cookies bleiben auch nach dem Schlie\xDFen des Endger\xE4ts gespeichert. So k\xF6nnen beispielsweise der Log-in-Status gespeichert und bevorzugte Inhalte direkt angezeigt werden, wenn der Nutzer eine Website erneut besucht. Ebenso k\xF6nnen die mithilfe von Cookies erhobenen Nutzerdaten zur Reichweitenmessung Verwendung finden. Sofern wir Nutzern keine expliziten Angaben zur Art und Speicherdauer von Cookies mitteilen (z.\xA0B. im Rahmen der Einholung der Einwilligung), sollten sie davon ausgehen, dass diese permanent sind und die Speicherdauer bis zu zwei Jahre betragen kann. "),n()(),t(289,"p")(290,"strong"),e(291,"Allgemeine Hinweise zum Widerruf und Widerspruch (Opt-out):\xA0"),n(),e(292,"Nutzer k\xF6nnen die von ihnen abgegebenen Einwilligungen jederzeit widerrufen und zudem einen Widerspruch gegen die Verarbeitung entsprechend den gesetzlichen Vorgaben, auch mittels der Privatsph\xE4re-Einstellungen ihres Browsers, erkl\xE4ren. "),n(),t(293,"ul",26)(294,"li")(295,"strong"),e(296,"Verarbeitete Datenarten:"),n(),e(297," Meta-, Kommunikations- und Verfahrensdaten (z. B. IP-Adressen, Zeitangaben, Identifikationsnummern, beteiligte Personen). "),n(),t(298,"li")(299,"strong"),e(300,"Betroffene Personen:"),n(),e(301," Nutzer (z.\xA0B. Webseitenbesucher, Nutzer von Onlinediensten). "),n(),t(302,"li",29)(303,"strong"),e(304,"Rechtsgrundlagen:"),n(),e(305," Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO). Einwilligung (Art. 6 Abs. 1 S. 1 lit. a) DSGVO). "),n()(),t(306,"p")(307,"strong"),e(308,"Weitere Hinweise zu Verarbeitungsprozessen, Verfahren und Diensten:"),n()(),t(309,"ul",26)(310,"li")(311,"strong"),e(312,"Verarbeitung von Cookie-Daten auf Grundlage einer Einwilligung: "),n(),e(313,"Wir setzen eine Einwilligungs-Management-L\xF6sung ein, bei der die Einwilligung der Nutzer zur Verwendung von Cookies oder zu den im Rahmen der Einwilligungs-Management-L\xF6sung genannten Verfahren und Anbietern eingeholt wird. Dieses Verfahren dient der Einholung, Protokollierung, Verwaltung und dem Widerruf von Einwilligungen, insbesondere bezogen auf den Einsatz von Cookies und vergleichbaren Technologien, die zur Speicherung, zum Auslesen und zur Verarbeitung von Informationen auf den Endger\xE4ten der Nutzer eingesetzt werden. Im Rahmen dieses Verfahrens werden die Einwilligungen der Nutzer f\xFCr die Nutzung von Cookies und die damit verbundenen Verarbeitungen von Informationen, einschlie\xDFlich der im Einwilligungs-Management-Verfahren genannten spezifischen Verarbeitungen und Anbieter, eingeholt. Die Nutzer haben zudem die M\xF6glichkeit, ihre Einwilligungen zu verwalten und zu widerrufen. Die Einwilligungserkl\xE4rungen werden gespeichert, um eine erneute Abfrage zu vermeiden und den Nachweis der Einwilligung gem\xE4\xDF der gesetzlichen Anforderungen f\xFChren zu k\xF6nnen. Die Speicherung erfolgt serverseitig und/oder in einem Cookie (sogenanntes Opt-In-Cookie) oder mittels vergleichbarer Technologien, um die Einwilligung einem spezifischen Nutzer oder dessen Ger\xE4t zuordnen zu k\xF6nnen. Sofern keine spezifischen Angaben zu den Anbietern von Einwilligungs-Management-Diensten vorliegen, gelten folgende allgemeine Hinweise: Die Dauer der Speicherung der Einwilligung betr\xE4gt bis zu zwei Jahre. Dabei wird ein pseudonymer Nutzer-Identifikator erstellt, der zusammen mit dem Zeitpunkt der Einwilligung, den Angaben zum Umfang der Einwilligung (z.\xA0B. betreffende Kategorien von Cookies und/oder Diensteanbieter) sowie Informationen \xFCber den Browser, das System und das verwendete Endger\xE4t gespeichert wird; "),t(314,"span",29)(315,"strong"),e(316,"Rechtsgrundlagen:"),n(),e(317," Einwilligung (Art. 6 Abs. 1 S. 1 lit. a) DSGVO)."),n()()(),t(318,"h2",31),e(319,"Registrierung, Anmeldung und Nutzerkonto"),n(),t(320,"p"),e(321," Nutzer k\xF6nnen ein Nutzerkonto anlegen. Im Rahmen der Registrierung werden den Nutzern die erforderlichen Pflichtangaben mitgeteilt und zu Zwecken der Bereitstellung des Nutzerkontos auf Grundlage vertraglicher Pflichterf\xFCllung verarbeitet. Zu den verarbeiteten Daten geh\xF6ren insbesondere die Login-Informationen (Nutzername, Passwort sowie eine E-Mail-Adresse). "),n(),t(322,"p"),e(323," Im Rahmen der Inanspruchnahme unserer Registrierungs- und Anmeldefunktionen sowie der Nutzung des Nutzerkontos speichern wir die IP-Adresse und den Zeitpunkt der jeweiligen Nutzerhandlung. Die Speicherung erfolgt auf Grundlage unserer berechtigten Interessen als auch jener der Nutzer an einem Schutz vor Missbrauch und sonstiger unbefugter Nutzung. Eine Weitergabe dieser Daten an Dritte erfolgt grunds\xE4tzlich nicht, es sei denn, sie ist zur Verfolgung unserer Anspr\xFCche erforderlich oder es besteht eine gesetzliche Verpflichtung hierzu. "),n(),t(324,"p"),e(325," Die Nutzer k\xF6nnen \xFCber Vorg\xE4nge, die f\xFCr deren Nutzerkonto relevant sind, wie z.\xA0B. technische \xC4nderungen, per E-Mail informiert werden. "),n(),t(326,"ul",26)(327,"li")(328,"strong"),e(329,"Verarbeitete Datenarten:"),n(),e(330," Bestandsdaten (z.\xA0B. der vollst\xE4ndige Name, Wohnadresse, Kontaktinformationen, Kundennummer, etc.); Kontaktdaten (z.\xA0B. Post- und E-Mail-Adressen oder Telefonnummern); Inhaltsdaten (z. B. textliche oder bildliche Nachrichten und Beitr\xE4ge sowie die sie betreffenden Informationen, wie z. B. Angaben zur Autorenschaft oder Zeitpunkt der Erstellung); Nutzungsdaten (z. B. Seitenaufrufe und Verweildauer, Klickpfade, Nutzungsintensit\xE4t und -frequenz, verwendete Ger\xE4tetypen und Betriebssysteme, Interaktionen mit Inhalten und Funktionen). Protokolldaten (z.\xA0B. Logfiles betreffend Logins oder den Abruf von Daten oder Zugriffszeiten.). "),n(),t(331,"li")(332,"strong"),e(333,"Betroffene Personen:"),n(),e(334," Nutzer (z.\xA0B. Webseitenbesucher, Nutzer von Onlinediensten). "),n(),t(335,"li")(336,"strong"),e(337,"Zwecke der Verarbeitung:"),n(),e(338," Erbringung vertraglicher Leistungen und Erf\xFCllung vertraglicher Pflichten; Sicherheitsma\xDFnahmen; Organisations- und Verwaltungsverfahren. Bereitstellung unseres Onlineangebotes und Nutzerfreundlichkeit. "),n(),t(339,"li")(340,"strong"),e(341,"Aufbewahrung und L\xF6schung:"),n(),e(342,' L\xF6schung entsprechend Angaben im Abschnitt "Allgemeine Informationen zur Datenspeicherung und L\xF6schung". L\xF6schung nach K\xFCndigung. '),n(),t(343,"li",29)(344,"strong"),e(345,"Rechtsgrundlagen:"),n(),e(346," Vertragserf\xFCllung und vorvertragliche Anfragen (Art. 6 Abs. 1 S. 1 lit. b) DSGVO). Berechtigte Interessen (Art. 6 Abs. 1 S. 1 lit. f) DSGVO). "),n()(),t(347,"p")(348,"strong"),e(349,"Weitere Hinweise zu Verarbeitungsprozessen, Verfahren und Diensten:"),n()(),t(350,"ul",26)(351,"li")(352,"strong"),e(353,"Profile der Nutzer sind nicht \xF6ffentlich: "),n(),e(354,"Die Profile der Nutzer sind \xF6ffentlich nicht sichtbar und nicht zug\xE4nglich. "),n(),t(355,"li")(356,"strong"),e(357,"L\xF6schung von Daten nach K\xFCndigung: "),n(),e(358,"Wenn Nutzer ihr Nutzerkonto gek\xFCndigt haben, werden deren Daten im Hinblick auf das Nutzerkonto, vorbehaltlich einer gesetzlichen Erlaubnis, Pflicht oder Einwilligung der Nutzer, gel\xF6scht; "),t(359,"span",29)(360,"strong"),e(361,"Rechtsgrundlagen:"),n(),e(362," Vertragserf\xFCllung und vorvertragliche Anfragen (Art. 6 Abs. 1 S. 1 lit. b) DSGVO)."),n()(),t(363,"li")(364,"strong"),e(365,"Keine Aufbewahrungspflicht f\xFCr Daten: "),n(),e(366,"Es obliegt den Nutzern, ihre Daten bei erfolgter K\xFCndigung vor dem Vertragsende zu sichern. Wir sind berechtigt, s\xE4mtliche w\xE4hrend der Vertragsdauer gespeicherte Daten des Nutzers unwiederbringlich zu l\xF6schen; "),t(367,"span",29)(368,"strong"),e(369,"Rechtsgrundlagen:"),n(),e(370," Vertragserf\xFCllung und vorvertragliche Anfragen (Art. 6 Abs. 1 S. 1 lit. b) DSGVO)."),n()()(),t(371,"h2",32),e(372,"\xC4nderung und Aktualisierung"),n(),t(373,"p"),e(374," Wir bitten Sie, sich regelm\xE4\xDFig \xFCber den Inhalt unserer Datenschutzerkl\xE4rung zu informieren. Wir passen die Datenschutzerkl\xE4rung an, sobald die \xC4nderungen der von uns durchgef\xFChrten Datenverarbeitungen dies erforderlich machen. Wir informieren Sie, sobald durch die \xC4nderungen eine Mitwirkungshandlung Ihrerseits (z.\xA0B. Einwilligung) oder eine sonstige individuelle Benachrichtigung erforderlich wird. "),n(),t(375,"p"),e(376," Sofern wir in dieser Datenschutzerkl\xE4rung Adressen und Kontaktinformationen von Unternehmen und Organisationen angeben, bitten wir zu beachten, dass die Adressen sich \xFCber die Zeit \xE4ndern k\xF6nnen und bitten die Angaben vor Kontaktaufnahme zu pr\xFCfen. "),n(),t(377,"h2",33),e(378,"Begriffsdefinitionen"),n(),t(379,"p"),e(380," In diesem Abschnitt erhalten Sie eine \xDCbersicht \xFCber die in dieser Datenschutzerkl\xE4rung verwendeten Begrifflichkeiten. Soweit die Begrifflichkeiten gesetzlich definiert sind, gelten deren gesetzliche Definitionen. Die nachfolgenden Erl\xE4uterungen sollen dagegen vor allem dem Verst\xE4ndnis dienen. "),n(),t(381,"ul",34)(382,"li")(383,"strong"),e(384,"Bestandsdaten:"),n(),e(385," Bestandsdaten umfassen wesentliche Informationen, die f\xFCr die Identifikation und Verwaltung von Vertragspartnern, Benutzerkonten, Profilen und \xE4hnlichen Zuordnungen notwendig sind. Diese Daten k\xF6nnen u.a. pers\xF6nliche und demografische Angaben wie Namen, Kontaktinformationen (Adressen, Telefonnummern, E-Mail-Adressen), Geburtsdaten und spezifische Identifikatoren (Benutzer-IDs) beinhalten. Bestandsdaten bilden die Grundlage f\xFCr jegliche formelle Interaktion zwischen Personen und Diensten, Einrichtungen oder Systemen, indem sie eine eindeutige Zuordnung und Kommunikation erm\xF6glichen. "),n(),t(386,"li")(387,"strong"),e(388,"Inhaltsdaten:"),n(),e(389," Inhaltsdaten umfassen Informationen, die im Zuge der Erstellung, Bearbeitung und Ver\xF6ffentlichung von Inhalten aller Art generiert werden. Diese Kategorie von Daten kann Texte, Bilder, Videos, Audiodateien und andere multimediale Inhalte einschlie\xDFen, die auf verschiedenen Plattformen und Medien ver\xF6ffentlicht werden. Inhaltsdaten sind nicht nur auf den eigentlichen Inhalt beschr\xE4nkt, sondern beinhalten auch Metadaten, die Informationen \xFCber den Inhalt selbst liefern, wie Tags, Beschreibungen, Autoreninformationen und Ver\xF6ffentlichungsdaten "),n(),t(390,"li")(391,"strong"),e(392,"Kontaktdaten:"),n(),e(393," Kontaktdaten sind essentielle Informationen, die die Kommunikation mit Personen oder Organisationen erm\xF6glichen. Sie umfassen u.a. Telefonnummern, postalische Adressen und E-Mail-Adressen, sowie Kommunikationsmittel wie soziale Medien-Handles und Instant-Messaging-Identifikatoren. "),n(),t(394,"li")(395,"strong"),e(396,"Meta-, Kommunikations- und Verfahrensdaten:"),n(),e(397," Meta-, Kommunikations- und Verfahrensdaten sind Kategorien, die Informationen \xFCber die Art und Weise enthalten, wie Daten verarbeitet, \xFCbermittelt und verwaltet werden. Meta-Daten, auch bekannt als Daten \xFCber Daten, umfassen Informationen, die den Kontext, die Herkunft und die Struktur anderer Daten beschreiben. Sie k\xF6nnen Angaben zur Dateigr\xF6\xDFe, dem Erstellungsdatum, dem Autor eines Dokuments und den \xC4nderungshistorien beinhalten. Kommunikationsdaten erfassen den Austausch von Informationen zwischen Nutzern \xFCber verschiedene Kan\xE4le, wie E-Mail-Verkehr, Anrufprotokolle, Nachrichten in sozialen Netzwerken und Chat-Verl\xE4ufe, inklusive der beteiligten Personen, Zeitstempel und \xDCbertragungswege. Verfahrensdaten beschreiben die Prozesse und Abl\xE4ufe innerhalb von Systemen oder Organisationen, einschlie\xDFlich Workflow-Dokumentationen, Protokolle von Transaktionen und Aktivit\xE4ten, sowie Audit-Logs, die zur Nachverfolgung und \xDCberpr\xFCfung von Vorg\xE4ngen verwendet werden. "),n(),t(398,"li")(399,"strong"),e(400,"Nutzungsdaten:"),n(),e(401," Nutzungsdaten beziehen sich auf Informationen, die erfassen, wie Nutzer mit digitalen Produkten, Dienstleistungen oder Plattformen interagieren. Diese Daten umfassen eine breite Palette von Informationen, die aufzeigen, wie Nutzer Anwendungen nutzen, welche Funktionen sie bevorzugen, wie lange sie auf bestimmten Seiten verweilen und \xFCber welche Pfade sie durch eine Anwendung navigieren. Nutzungsdaten k\xF6nnen auch die H\xE4ufigkeit der Nutzung, Zeitstempel von Aktivit\xE4ten, IP-Adressen, Ger\xE4teinformationen und Standortdaten einschlie\xDFen. Sie sind besonders wertvoll f\xFCr die Analyse des Nutzerverhaltens, die Optimierung von Benutzererfahrungen, das Personalisieren von Inhalten und das Verbessern von Produkten oder Dienstleistungen. Dar\xFCber hinaus spielen Nutzungsdaten eine entscheidende Rolle beim Erkennen von Trends, Vorlieben und m\xF6glichen Problembereichen innerhalb digitaler Angebote "),n(),t(402,"li")(403,"strong"),e(404,"Personenbezogene Daten:"),n(),e(405,' "Personenbezogene Daten" sind alle Informationen, die sich auf eine identifizierte oder identifizierbare nat\xFCrliche Person (im Folgenden "betroffene Person") beziehen; als identifizierbar wird eine nat\xFCrliche Person angesehen, die direkt oder indirekt, insbesondere mittels Zuordnung zu einer Kennung wie einem Namen, zu einer Kennnummer, zu Standortdaten, zu einer Online-Kennung (z.\xA0B. Cookie) oder zu einem oder mehreren besonderen Merkmalen identifiziert werden kann, die Ausdruck der physischen, physiologischen, genetischen, psychischen, wirtschaftlichen, kulturellen oder sozialen Identit\xE4t dieser nat\xFCrlichen Person sind. '),n(),t(406,"li")(407,"strong"),e(408,"Protokolldaten:"),n(),e(409," Protokolldaten sind Informationen \xFCber Ereignisse oder Aktivit\xE4ten, die in einem System oder Netzwerk protokolliert wurden. Diese Daten enthalten typischerweise Informationen wie Zeitstempel, IP-Adressen, Benutzeraktionen, Fehlermeldungen und andere Details \xFCber die Nutzung oder den Betrieb eines Systems. Protokolldaten werden oft zur Analyse von Systemproblemen, zur Sicherheits\xFCberwachung oder zur Erstellung von Leistungsberichten verwendet. "),n(),t(410,"li")(411,"strong"),e(412,"Verantwortlicher:"),n(),e(413,' Als "Verantwortlicher" wird die nat\xFCrliche oder juristische Person, Beh\xF6rde, Einrichtung oder andere Stelle, die allein oder gemeinsam mit anderen \xFCber die Zwecke und Mittel der Verarbeitung von personenbezogenen Daten entscheidet, bezeichnet. '),n(),t(414,"li")(415,"strong"),e(416,"Verarbeitung:"),n(),e(417,' "Verarbeitung" ist jeder mit oder ohne Hilfe automatisierter Verfahren ausgef\xFChrte Vorgang oder jede solche Vorgangsreihe im Zusammenhang mit personenbezogenen Daten. Der Begriff reicht weit und umfasst praktisch jeden Umgang mit Daten, sei es das Erheben, das Auswerten, das Speichern, das \xDCbermitteln oder das L\xF6schen. '),n()(),t(418,"p",35)(419,"a",36),e(420,"Erstellt mit kostenlosem Datenschutz-Generator.de von Dr. Thomas Schwenke"),n()()())},styles:["*[_ngcontent-%COMP%]{color:#fff}div[_ngcontent-%COMP%]{max-width:1440px;margin-inline:96px}a[_ngcontent-%COMP%]{color:#fff}h1[_ngcontent-%COMP%]{font-size:32px}h2[_ngcontent-%COMP%]{font-size:24px}p[_ngcontent-%COMP%]{font-size:16px}@media (max-width: 560px){div[_ngcontent-%COMP%]{margin-inline:16px}h1[_ngcontent-%COMP%]{font-size:24px}h2[_ngcontent-%COMP%]{font-size:20px}}@media (max-width: 560px){div[_ngcontent-%COMP%]{margin-inline:5px}}"]})};export{d as Legalnotice};
